* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
  /*  font-family: 'Poppins', sans-serif; */
}

body {
  width: 100%;
  height: 100%;
  background-color: #f8f8f8;
  font-family: 'Noto Sans KR', sans-serif;
  overflow-x: hidden;
  overflow-y: hidden;
}

li {
  list-style-type: none;
}

a {
  text-decoration: none;
  color: #222;
}

.pop {
  font-family: 'Poppins', sans-serif;
}

$color : #FD8234;

@mixin size($width, $height) {
  width: $width;
  height: $height;
}

.container {
  width: 100%;
  //width: 1420px;
  height: auto;
  margin-top: 80px;


  .contents_wrap {
    position: relative;
    @include size(1420px, calc(100vh - 80px));
    margin: 0 auto;
    display: flex;

    .left_section {
      @include size(820px, auto);
      //@include size(calc(100vw - 600px), auto);
      overflow-y: auto;
      border-right: 1px solid #555;
      overflow-x: hidden;

      &::-webkit-scrollbar {
        width: 14px; //스크롤바 넓이
      }

      &::-webkit-scrollbar-track {
        background-color: #f8f8f8; //트랙 배경 색상
      }

      &::-webkit-scrollbar-thumb {
        background-color: $color; //바 색상
      }

      // &::-webkit-scrollbar-button:start:decrement,
      // &::-webkit-scrollbar-button:end:increment {
      //   width: 10px;

      .detail_swiper {
        width: 820px;
        height: 820px;
        position: relative;


        .swiper-slide img {
          width: 100%;
          height: 100%;
          object-fit: cover
        }
      }

      .swiperBtn {
        color: #ccc;
        opacity: 0.2;

        &:hover {
          opacity: 1;
        }
      }

      .swiper-button-next {
        position: absolute;
        right: 0;
        left: 770px;

      }

      .swiper-pagination {
        .swiper-pagination-bullet {
          @include size(10px, 10px);
        }

        .swiper-pagination-bullet-active {
          background-color: #261C13;
        }

        .swiper-pagination-bullet:hover {
          transform: scale(1.1);
          transition: all 0.3s;
        }
      }
    }

    .content_tab {
      position: sticky;
      top: 0;

      .tabmenu_list {
        position: absolute;
        @include size(820px, 85px);
        display: flex;
        justify-content: space-around;
        align-items: center;
        text-align: center;
        background-color: #fff;
        border: 1px solid #ccc;


        .tabmenu {
          position: relative;
          width: 210px;
          height: 100%;
          line-height: 85px;
          cursor: pointer;
        }

        .bar {
          position: absolute;
          bottom: 0;
          left: 0;
          height: 5px;
          background: $color;
        }
      }
    }

    .detail {
      .detail_wrap {
        background-color: #efefef;
        padding-top: 70px;

        h1 {
          font-size: 28px;
          font-weight: bold;
          padding: 70px 0 30px 45px;
        }

        p {
          font-size: 14px;
          padding: 0 75px 65px 45px;
        }

        img {
          //width: 820px;
          width: 100%;
          height: auto;
        }
      }
    }
  }

  .right_section {
    //@include size(calc(100vw - 820px), auto);
    @include size(600px, auto);
    background-color: #fff;
    overflow-y: auto;
    overflow-x: hidden;

    &::-webkit-scrollbar {
      width: 14px; //스크롤바 넓이
    }

    &::-webkit-scrollbar-track {
      background-color: #fff; //트랙 배경 색상
    }

    &::-webkit-scrollbar-thumb {
      background-color: #FD8234; //바 색상
    }

    .info_area {
      height: 210px;
      border-bottom: 1px solid #999;

      .info_txt {
        padding: 30px 66px 42px 66px;

        h3 {
          font-size: 20px;
          font-weight: bold;
        }

        p {
          font-size: 24px;
          font-weight: 300;
          padding-bottom: 32px;
        }

        span {
          font-size: 20px;
          font-weight: bold;
        }
      }
    }

    .color_area {
      height: 60px;
      padding: 12px 66px;
      display: flex;
      align-items: center;
      border-bottom: 1px solid #999;

      .color_option {
        display: flex;
        align-items: center;

        li {
          width: 25px;
          height: 25px;
          border-radius: 50%;
          border: 1px solid #eee;
          margin-right: 10px;
        }

        >.butter {
          background-color: #F9E0BB;
        }

        >.peach {
          background-color: #FCE9F1;
        }

        >.iceblue {
          background-color: #fff;
          margin-right: 20px;
        }
      }

      .color_choice {
        font-size: 13px;
        font-weight: bold;
      }
    }
  }
}


.add_product_area {
  position: relative;
  height: 285px;
  padding: 30px 70px;
  border-bottom: 1px solid #555;

  p {
    font-size: 20px;
